mapper.insert:
INSERT INTO posts_hashtags (postId, hashtagId)
SELECT #{postId}, idt.id FROM (
    SELECT id FROM hashtags WHERE
    
        hashtag=#{text}
    
) AS idt

mapper.delete:
START TRANSACTION;
DELETE FROM talelorz.likes_by WHERE 
    postId = ${postId} AND userId = #{userId};
SELECT ROW_COUNT();
COMMIT;

mapper.update:
UPDATE posts_likes
    SET likes=(SELECT IF( likes-1 < 0 , 0, likes-1) AS likes_sanitized)
    WHERE postId = ${postId}
